import React, { ReactNode } from 'react';
import './header.scss';

export type HeaderProps = {
  /**
   * a node to be rendered in the special component.
   */
  children?: ReactNode;
  className?: string;
};

export function Header({ children, className }: HeaderProps) {
  return (
    <h1 className={className}>
      {children}
    </h1>
  );
}
